mapper.select:
SELECT TG.ID AS ID,TV.ID AS VID,TV.CARNUMBER ,TV.CONTACTS,TV.CONTACTSTEL,TV.CLASSIFY,TV.CARTYPE, TC.COMPNAME AS COMPANYNAME, TG.EQUITMENT,TG.SIMNUMBER,TG.FACILITATOR,TG.NETWORKNO,TG.AZZM,TG.INSTALLTIME,TG.INSTALLER,TG.ENDRIQI,TG.REMARK ,TG.INSADDERSS FROM TB_VEHICLE TV LEFT JOIN (SELECT ID,vid,max(ENDRIQI)as ENDRIQI,EQUITMENT,SIMNUMBER,FACILITATOR,NETWORKNO,AZZM, INSTALLTIME,INSTALLER,INSADDERSS,REMARK FROM TB_GLOBAL GROUP BY vid) TG ON TV.ID = TG.VID LEFT JOIN TB_COMPANY TC on TV.companyId = TC.ID
AND TV.CARNUMBER LIKE CONCAT('%',#{carNumber},'%')
AND TC.COMPNAME LIKE CONCAT('%',#{companName},'%')
AND TG.ENDRIQI >= #{endDate} AND TG.ENDRIQI <= #{endDate1}
LIMIT #{startNum},#{endNum}

SELECT VID,CARNUMBER,endriqi FROM(SELECT A.*,ROW_NUMBER() OVER (ORDER BY endriqi DESC) ROWNUM FROM(
	SELECT TV.ID AS VID, TV.CARNUMBER,C.endriqi
		FROM TB_VEHICLE TV   JOIN( SELECT MAX (endriqi) AS endriqi, VID FROM tb_global GROUP BY VID )C ON TV.ID = C.VID
		where  endriqi<dateadd(DAY,20,getdate()) 
			
				AND TV.OPERATORID =#{operId}
			
			
				AND TV.depId  LIKE   #{depId}  + '%'
			
			
				AND TV.OWNEDID =#{companyId}
			
	) A)B
	WHERE ROWNUM > #{startNum} AND ROWNUM <=#{endNum}

SELECT  COUNT(0) FROM TB_VEHICLE TV   JOIN( SELECT MAX (endriqi) AS endriqi, VID FROM tb_global GROUP BY VID )C ON TV.ID = C.VID
	where  endriqi<dateadd(DAY,20,getdate()) 
		
			AND TV.OPERATORID =#{operId}
		
		
			AND TV.depId  LIKE   #{depId}  + '%'
		
		
			AND TV.OWNEDID =#{companyId}

 SELECT *  from TB_GLOBAL 
 
	
		AND  vId =#{vId}
	

 LIMIT #{startNum},#{endNum}
