mapper.select:
SELECT  FROM TB_PROVE
	
		AND number like CONCAT('%',#{number},'%')
		AND address LIKE CONCAT('%',#{address},'%')
		 AND usman LIKE CONCAT('%',#{usman},'%') 
		AND riqi >= #{endDate} 
		 AND riqi <= #{endDate1} 
		AND STATE =  #{state} 
	
LIMIT #{startPage},#{endPage}

SELECT COUNT(1) FROM TB_PROVE
	
		AND number like CONCAT('%',#{number},'%')
		AND address LIKE CONCAT('%',#{address},'%')
		 AND usman LIKE CONCAT('%',#{usman},'%') 
		AND riqi >= #{endDate} 
		 AND riqi <= #{endDate1} 
		AND STATE =  #{state}
